    "ID": 1,
    "Name": "John Smith",
    "Details": {
        "Age": 28,
        "Address": {
            "City": "New York",
            "PostalCode": "10001"
        }
    },
    "Department": "Sales",
    "Salary": 50000,
    "HireDate": "2018-05-21"
}

# Flatten nested layers
df = json_normalize(data, sep='_')
print(df.to_string())
